import React  from "react";
import CardList from "./CardList"
import Searchbox from './searchbox';
import { robots } from './robots';
import'./App.css';

class  App extends React.Component{
    constructor(){
        super()
        this.state={
            robots2: robots,
            searchfilled: ''
        }
    }
    onSearchChange = (event) => {
        this.setState({searchfilled: event.target.value})
    }
    render(){
        const filteredRobots = this.state.robots2.filter(robots => {
            return robots.name.toLowerCase().includes(this.state.searchfilled.toLowerCase())
        })
    return(

        <div className="tc">
            <h1>RoboFriends</h1>
            <Searchbox  search2={this.onSearchChange}/>
        <CardList robots2 = { filteredRobots }  />
        </div>
    )
}
}
export default App
